using Kerberos.NET;
using Kerberos.NET.Crypto;
using Microsoft.VisualStudio.TestTools.UnitTesting;
using System.IO;
using System.Threading.Tasks;

namespace Tests.Kerberos.NET
{
    [TestClass]
    public class AuthenticatorTests
    {
        private const string RC4Header = "Negotiate ";

        [TestMethod]
        public async Task TestAuthenticator()
        {
            var authenticator = new KerberosAuthenticator(new KerberosValidator(new KeyTable(File.ReadAllBytes("data\\sample.keytab"))) { ValidateAfterDecrypt = ValidationActions.Replay });

            Assert.IsNotNull(authenticator);

            var result = await authenticator.Authenticate(RC4Header);

            Assert.IsNotNull(result);

            Assert.AreEqual("Administrator@identityintervention.com", result.Name);
        }
    }
}
